    {
        // Add a record to the root domain
        var foobar = new DNSimple.Record("foobar", new DNSimple.RecordArgs
        {
            Domain = @var.Dnsimple_domain,
            Name = "",
            Ttl = 3600,
            Type = "A",
            Value = "192.168.0.11",
        });
    }

}
```
